@import url('https://fonts.googleapis.com/css2?family=Nunito:wght@300;400;500;600;700;800&family=Roboto:wght@300;400;500;600;700&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  /* Fresh, clean farmer-friendly theme */
  --background: hsl(95, 20%, 97%);
  --foreground: hsl(95, 25%, 15%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(95, 25%, 15%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(95, 25%, 15%);
  --primary: hsl(95, 60%, 45%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(95, 15%, 92%);
  --secondary-foreground: hsl(95, 25%, 25%);
  --muted: hsl(95, 10%, 95%);
  --muted-foreground: hsl(95, 8%, 45%);
  --accent: hsl(35, 85%, 55%);
  --accent-foreground: hsl(0, 0%, 100%);
  --destructive: hsl(0, 75%, 55%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(95, 15%, 88%);
  --input: hsl(95, 10%, 95%);
  --ring: hsl(95, 60%, 45%);
  --success: hsl(120, 60%, 50%);
  --warning: hsl(35, 85%, 55%);
  --info: hsl(210, 80%, 60%);
  --font-sans: 'Nunito', 'Roboto', system-ui, sans-serif;
  --radius: 0.5rem;
}

.dark {
  --background: hsl(95, 10%, 8%);
  --foreground: hsl(95, 5%, 95%);
  --card: hsl(95, 8%, 12%);
  --card-foreground: hsl(95, 5%, 95%);
  --popover: hsl(95, 8%, 12%);
  --popover-foreground: hsl(95, 5%, 95%);
  --primary: hsl(95, 65%, 55%);
  --primary-foreground: hsl(95, 10%, 8%);
  --secondary: hsl(95, 8%, 18%);
  --secondary-foreground: hsl(95, 5%, 75%);
  --muted: hsl(95, 8%, 15%);
  --muted-foreground: hsl(95, 5%, 55%);
  --accent: hsl(35, 80%, 60%);
  --accent-foreground: hsl(95, 10%, 8%);
  --destructive: hsl(0, 70%, 60%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(95, 8%, 22%);
  --input: hsl(95, 8%, 15%);
  --ring: hsl(95, 65%, 55%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

@layer components {
  /* Clean farmer-friendly card styles */
  .farm-card {
    @apply bg-card border border-border rounded-lg shadow-sm transition-all duration-200 ease-in-out;
  }

  .farm-card:hover {
    @apply shadow-md border-primary -translate-y-0.5;
  }

  /* Simple button variants */
  .btn-farm-primary {
    @apply bg-primary text-primary-foreground px-4 py-2 rounded-lg font-medium transition-colors;
  }

  .btn-farm-primary:hover {
    @apply bg-primary opacity-90;
  }

  .btn-farm-secondary {
    @apply bg-secondary text-secondary-foreground px-4 py-2 rounded-lg font-medium transition-colors;
  }

  .btn-farm-secondary:hover {
    @apply bg-secondary opacity-80;
  }

  /* Clean input styles */
  .input-farm {
    @apply bg-input border border-border rounded-lg px-3 py-2 text-foreground placeholder:text-muted-foreground focus:ring-2 focus:ring-primary focus:border-primary transition-all;
  }

  /* Section headers */
  .section-header {
    @apply text-xl font-semibold text-foreground mb-4 flex items-center gap-2;
  }

  /* Status badges */
  .status-success {
    @apply bg-green-100 text-green-800 px-2 py-1 rounded-full text-xs font-medium;
  }

  .status-warning {
    @apply bg-yellow-100 text-yellow-800 px-2 py-1 rounded-full text-xs font-medium;
  }

  .status-info {
    @apply bg-blue-100 text-blue-800 px-2 py-1 rounded-full text-xs font-medium;
  }
}

@layer utilities {
  @keyframes glowRotate {
    0% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
    100% { background-position: 0% 50%; }
  }

  @keyframes float {
    0%, 100% { 
      transform: translateY(0px) rotate(0deg); 
      opacity: 0.6;
    }
    50% { 
      transform: translateY(-20px) rotate(180deg); 
      opacity: 1;
    }
  }

  @keyframes pulse {
    0%, 100% { 
      opacity: 0.4;
      transform: scale(1);
    }
    50% { 
      opacity: 1;
      transform: scale(1.1);
    }
  }

  @keyframes slideUpFade {
    from {
      opacity: 0;
      transform: translateY(40px) scale(0.95);
    }
    to {
      opacity: 1;
      transform: translateY(0) scale(1);
    }
  }

  @keyframes scaleIn {
    from {
      opacity: 0;
      transform: scale(0.8) rotate(-10deg);
    }
    to {
      opacity: 1;
      transform: scale(1) rotate(0deg);
    }
  }

  @keyframes morphing {
    0%, 100% {
      border-radius: 30% 70% 70% 30% / 30% 30% 70% 70%;
      transform: rotate(0deg);
    }
    50% {
      border-radius: 70% 30% 30% 70% / 70% 70% 30% 30%;
      transform: rotate(180deg);
    }
  }

  @keyframes bounce {
    0%, 100% {
      transform: translateY(-25%);
      animation-timing-function: cubic-bezier(0.8, 0, 1, 1);
    }
    50% {
      transform: translateY(0);
      animation-timing-function: cubic-bezier(0, 0, 0.2, 1);
    }
  }

  @keyframes spin {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
  }

  @keyframes twinkle {
    0%, 100% { opacity: 0.3; }
    50% { opacity: 1; }
  }

  .animate-float {
    animation: float 4s ease-in-out infinite;
  }

  .animate-pulse-slow {
    animation: pulse 4s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  .animate-slide-up {
    animation: slideUpFade 0.8s ease-out forwards;
  }

  .animate-scale-in {
    animation: scaleIn 0.6s ease-out forwards;
  }

  .animate-morphing {
    animation: morphing 8s ease-in-out infinite;
  }

  .animate-twinkle {
    animation: twinkle 2s ease-in-out infinite;
  }

  /* Premium hover effects */
  .hover-glow {
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .hover-glow:hover {
    transform: translateY(-6px) scale(1.03);
    box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.8), 0 0 40px rgba(34, 197, 94, 0.4);
    filter: brightness(1.1);
  }

  .hover-magnetic {
    transition: transform 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .hover-magnetic:hover {
    transform: perspective(1000px) rotateX(5deg) rotateY(5deg) scale3d(1.05, 1.05, 1.05);
  }

  .hover-shine {
    position: relative;
    overflow: hidden;
  }

  .hover-shine::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
