  /* Gradient text utilities */
  .gradient-text-primary {
    background: linear-gradient(135deg, hsl(142, 90%, 55%) 0%, hsl(47, 96%, 65%) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    animation: glowRotate 3s linear infinite;
    background-size: 200% 200%;
  }

  .gradient-text-accent {
    background: linear-gradient(135deg, hsl(47, 96%, 65%) 0%, hsl(210, 98%, 60%) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  /* Glass morphism enhanced */
  .glass-dark {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.1);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
  }

  .text-shadow-glow {
    text-shadow: 0 0 20px rgba(34, 197, 94, 0.6), 0 0 40px rgba(34, 197, 94, 0.4), 0 0 60px rgba(34, 197, 94, 0.2);
  }
}

