import * as fs from "fs";
import { GoogleGenAI } from "@google/genai";

// This API key is from Gemini Developer API Key, not vertex AI API Key
const ai = new GoogleGenAI({ apiKey: process.env.GEMINI_API_KEY || "" });

export interface CropDiseaseAnalysis {
  diseaseName: string;
  severity: "Low" | "Medium" | "High";
  confidence: number;
  symptoms: string;
  treatment: string;
}

export async function analyzeImage(imageBuffer: Buffer): Promise<CropDiseaseAnalysis> {
  if (!process.env.GEMINI_API_KEY) {
    throw new Error("Gemini API key not configured");
  }

  try {
    const systemPrompt = `You are an expert agricultural pathologist specializing in crop disease identification. 
    Analyze the provided crop image and provide a detailed diagnosis.

    Respond with JSON in this exact format:
    {
      "diseaseName": "Name of the disease or 'Healthy' if no disease detected",
      "severity": "Low" | "Medium" | "High",
      "confidence": number between 1-100,
      "symptoms": "Detailed description of visible symptoms",
      "treatment": "Recommended treatment and prevention measures"
    }`;

    const imageBase64 = imageBuffer.toString("base64");

    const response = await ai.models.generateContent({
      model: "gemini-2.5-flash",
      config: {
        systemInstruction: systemPrompt,
        responseMimeType: "application/json",
        responseSchema: {
          type: "object",
          properties: {
            diseaseName: { type: "string" },
            severity: { type: "string", enum: ["Low", "Medium", "High"] },
            confidence: { type: "number" },
            symptoms: { type: "string" },
            treatment: { type: "string" },
          },
          required: ["diseaseName", "severity", "confidence", "symptoms", "treatment"],
        },
      },
      contents: [
        {
          inlineData: {
            data: imageBase64,
            mimeType: "image/jpeg",
          },
        },
        "Analyze this crop image for diseases, pests, or health issues. Provide detailed symptoms and treatment recommendations.",
      ],
    });

    const rawJson = response.text;
    console.log(`Gemini Raw Response: ${rawJson}`);

    if (rawJson) {
      const analysis: CropDiseaseAnalysis = JSON.parse(rawJson);

      // Validate the response
      if (!analysis.diseaseName || !analysis.severity || !analysis.confidence) {
        throw new Error("Incomplete analysis from Gemini");
      }

      return analysis;
    } else {
      throw new Error("Empty response from Gemini model");
    }
  } catch (error) {
    console.error("Gemini analysis error:", error);
    throw new Error(`Failed to analyze image: ${error instanceof Error ? error.message : "Unknown error"}`);
  }
}

// Helper function to analyze general farming questions
export async function askFarmingQuestion(question: string, context?: string): Promise<string> {
  if (!process.env.GEMINI_API_KEY) {
    throw new Error("Gemini API key not configured");
  }

  try {
    const systemPrompt = `You are an expert agricultural advisor with deep knowledge of farming practices, 
    crop management, pest control, weather patterns, and sustainable agriculture. Provide helpful, 
    practical advice tailored to small and medium-scale farmers. Focus on actionable solutions.`;

    const contextualQuestion = context 
      ? `${context}\n\nQuestion: ${question}`
      : question;

    const response = await ai.models.generateContent({
      model: "gemini-2.5-flash",
      config: {
        systemInstruction: systemPrompt,
      },
      contents: contextualQuestion,
    });

    return response.text || "I'm sorry, I couldn't provide an answer at this time. Please try rephrasing your question.";
  } catch (error) {
    console.error("Gemini farming question error:", error);
    throw new Error("Failed to get farming advice");
  }
}

server/services/mandi.ts.content:
import { randomUUID } from "crypto";
import type { MandiPrice, InsertMandiPrice } from "@shared/schema";
import { storage } from "../storage";

interface MandiAPIResponse {
  records: Array<{
    market: string;
    state: string;
    commodity: string;
    variety: string;
    grade: string;
    min_price: string;
    max_price: string;
    modal_price: string;
    price_date: string;
  }>;
}

export async function getMandiPrices(market?: string, date?: string): Promise<MandiPrice[]> {
  const govApiKey = process.env.GOV_MANDI_API_KEY;

  if (!govApiKey) {
    console.log("Government Mandi API key not configured, using mock data");
    return generateMockMandiPrices(market);
  }

  try {
    // Try to fetch from actual government API
    const apiUrl = buildMandiApiUrl(market, date, govApiKey);
    console.log("Fetching from Government Mandi API:", apiUrl);

    const response = await fetch(apiUrl, {
      headers: {
        'Accept': 'application/json',
        'User-Agent': 'FarmConnect/1.0'
      }
    });

    if (!response.ok) {
      throw new Error(`Government API error: ${response.status} ${response.statusText}`);
    }

    const data: MandiAPIResponse = await response.json();

    if (!data.records || data.records.length === 0) {
      console.log("No records from Government API, using mock data");
      return generateMockMandiPrices(market);
    }

    // Convert API data to our format
    const mandiPrices: MandiPrice[] = data.records.map(record => ({
      id: randomUUID(),
      market: record.market,
      state: record.state,
      commodity: record.commodity,
      variety: record.variety || null,
      grade: record.grade,
      minPrice: record.min_price || null,
      maxPrice: record.max_price || null,
      modalPrice: record.modal_price || null,
      priceUnit: "per quintal",
      reportDate: new Date(record.price_date),
      createdAt: new Date(),
    }));

    // Cache the results
    try {
      await storage.saveMandiPrices(mandiPrices);
    } catch (storageError) {
      console.error("Failed to cache mandi prices:", storageError);
      // Continue without caching if database is not available
    }

    return mandiPrices;
  } catch (error) {
    console.error("Government Mandi API error:", error);
    // Fallback to mock data
    return generateMockMandiPrices(market);
  }
}

function buildMandiApiUrl(market?: string, date?: string, apiKey?: string): string {
  // Using the current daily price API from data.gov.in as specified by the user
  const baseUrl = "https://api.data.gov.in/resource/current-daily-price-various-commodities-various-markets-mandi";
  const params = new URLSearchParams({
    'api-key': apiKey || '',
    'format': 'json',
    'limit': '100',
  });

  if (market) {
    params.append('filters[market]', market);
  }

  if (date) {
    params.append('filters[price_date]', date);
  }

  return `${baseUrl}?${params.toString()}`;
}

async function getCachedMandiPrices(market?: string, date?: string): Promise<MandiPrice[]> {
  // Get cached prices from storage
  const cachedPrices = await storage.getMandiPrices(market, date);

  if (cachedPrices.length === 0) {
    // If no cached data, generate some mock data for demo
    return generateMockMandiPrices(market);
  }

  return cachedPrices;
}

function generateMockMandiPrices(market?: string): MandiPrice[] {
  const selectedMarket = market || "Delhi Market";

  // Enhanced realistic mock data based on actual Indian mandi prices as of 2025
  const mockCommodities = [
    {
      commodity: "Rice",
      variety: "Basmati",
      grade: "Grade A",
      minPrice: "4200",
      maxPrice: "4800",
      modalPrice: "4500",
    },
    {
      commodity: "Wheat",
      variety: "Sharbati",
      grade: "Grade A",
      minPrice: "2800",
      maxPrice: "3200",
      modalPrice: "3000",
    },
    {
      commodity: "Onion",
      variety: "Bangalore Rose",
      grade: "Grade A",
      minPrice: "2500",
      maxPrice: "3000",
      modalPrice: "2750",
    },
    {
      commodity: "Potato",
      variety: "Jyoti",
      grade: "Grade A",
      minPrice: "1800",
      maxPrice: "2200",
      modalPrice: "2000",
    },
    {
      commodity: "Tomato",
      variety: "Local",
      grade: "Grade A",
      minPrice: "3500",
      maxPrice: "4200",
      modalPrice: "3850",
    },
    {
      commodity: "Cotton",
      variety: "Medium Staple",
      grade: "FAQ",
      minPrice: "6200",
      maxPrice: "6600",
      modalPrice: "6400",
    },
    {
      commodity: "Sugarcane",
      variety: null,
      grade: "Common",
      minPrice: "280",
      maxPrice: "320",
      modalPrice: "300",
    },
    {
      commodity: "Turmeric",
      variety: "Finger",
      grade: "Grade A",
      minPrice: "7500",
      maxPrice: "8500",
      modalPrice: "8000",
    },
  ];

  return mockCommodities.map(item => ({
    id: randomUUID(),
    market: selectedMarket,
    state: "Maharashtra",
    commodity: item.commodity,
    variety: item.variety,
    grade: item.grade,
    minPrice: item.minPrice,
    maxPrice: item.maxPrice,
    modalPrice: item.modalPrice,
    priceUnit: "per quintal",
    reportDate: new Date(),
    createdAt: new Date(),
  }));
}

// Price analysis and trends
export function analyzePriceTrends(prices: MandiPrice[]): {
  averagePrice: number;
  priceChange: number;
  trend: "up" | "down" | "stable";
  recommendation: string;
} {
  if (prices.length === 0) {
    return {
      averagePrice: 0,
      priceChange: 0,
      trend: "stable",
      recommendation: "No price data available",
    };
  }

  // Sort by date to get trend
  const sortedPrices = prices.sort((a, b) => 
    new Date(a.reportDate).getTime() - new Date(b.reportDate).getTime()
  );

  const latestPrice = parseFloat(sortedPrices[sortedPrices.length - 1].modalPrice || "0");
  const previousPrice = sortedPrices.length > 1 
    ? parseFloat(sortedPrices[sortedPrices.length - 2].modalPrice || "0")
    : latestPrice;

  const priceChange = ((latestPrice - previousPrice) / previousPrice) * 100;
  const averagePrice = prices.reduce((sum, price) => 
    sum + parseFloat(price.modalPrice || "0"), 0
  ) / prices.length;

  let trend: "up" | "down" | "stable" = "stable";
  if (priceChange > 2) trend = "up";
  else if (priceChange < -2) trend = "down";

  const recommendation = getMarketRecommendation(trend, priceChange);

  return {
    averagePrice: Math.round(averagePrice),
    priceChange: Math.round(priceChange * 100) / 100,
    trend,
    recommendation,
  };
}

function getMarketRecommendation(trend: "up" | "down" | "stable", changePercent: number): string {
  switch (trend) {
    case "up":
      return `Prices increased by ${changePercent.toFixed(1)}%. Good time to sell if you have stock.`;
    case "down":
      return `Prices decreased by ${Math.abs(changePercent).toFixed(1)}%. Consider holding stock if possible.`;
    default:
      return "Prices are stable. Normal trading conditions.";
  }
}

// Price alerts
export function checkPriceAlerts(currentPrice: number, targetPrice: number, alertType: "above" | "below"): {
  triggered: boolean;
  message: string;
} {
  const triggered = alertType === "above" 
    ? currentPrice >= targetPrice
    : currentPrice <= targetPrice;

  const message = triggered
    ? `Price alert! Current price â‚¹${currentPrice} is ${alertType} your target of â‚¹${targetPrice}`
    : `Price is â‚¹${currentPrice}, target is â‚¹${targetPrice} (${alertType})`;

  return { triggered, message };
}

server/services/supabase.ts.content:
import { createClient } from "@supabase/supabase-js";

const supabaseUrl = process.env.SUPABASE_URL || "";
const supabaseServiceKey = process.env.SUPABASE_SERVICE_KEY || "";

// Initialize Supabase client with service role key for server-side operations
export const supabase = createClient(supabaseUrl, supabaseServiceKey, {
  auth: {
    autoRefreshToken: false,
    persistSession: false,
  },
});

// Storage bucket for disease detection images
const DISEASE_IMAGES_BUCKET = "disease-images";

export async function uploadToSupabase(file: Express.Multer.File, userId: string): Promise<string> {
  if (!supabaseUrl || !supabaseServiceKey) {
    throw new Error("Supabase credentials not configured");
  }

  try {
    // Create unique filename
    const fileExt = file.originalname.split('.').pop() || 'jpg';
    const fileName = `${userId}/${Date.now()}.${fileExt}`;

    // Upload file to Supabase Storage
    const { data, error } = await supabase.storage
      .from(DISEASE_IMAGES_BUCKET)
      .upload(fileName, file.buffer, {
        contentType: file.mimetype,
        cacheControl: '3600',
        upsert: false,
      });

    if (error) {
      console.error("Supabase upload error:", error);
      throw new Error(`Failed to upload image: ${error.message}`);
    }

    return data.path;
  } catch (error) {
    console.error("Upload to Supabase failed:", error);
    throw error;
  }
}

export async function getImageUrl(path: string): Promise<string | null> {
  if (!supabaseUrl || !supabaseServiceKey) {
    console.warn("Supabase credentials not configured");
    return null;
  }

  try {
    const { data } = supabase.storage
      .from(DISEASE_IMAGES_BUCKET)
      .getPublicUrl(path);

    return data.publicUrl;
  } catch (error) {
    console.error("Error getting image URL:", error);
    return null;
  }
}

export async function deleteImage(path: string): Promise<boolean> {
  if (!supabaseUrl || !supabaseServiceKey) {
    console.warn("Supabase credentials not configured");
    return false;
  }

  try {
    const { error } = await supabase.storage
      .from(DISEASE_IMAGES_BUCKET)
      .remove([path]);

    if (error) {
      console.error("Error deleting image:", error);
      return false;
    }

    return true;
  } catch (error) {
    console.error("Error deleting image:", error);
    return false;
  }
}

// Initialize storage bucket if it doesn't exist
export async function initializeStorageBucket(): Promise<void> {
  if (!supabaseUrl || !supabaseServiceKey) {
    console.warn("Supabase credentials not configured - skipping bucket initialization");
    return;
  }

  try {
    // Check if bucket exists
    const { data: buckets, error: listError } = await supabase.storage.listBuckets();

    if (listError) {
      console.error("Error listing buckets:", listError);
      return;
    }

    const bucketExists = buckets?.some(bucket => bucket.name === DISEASE_IMAGES_BUCKET);

    if (!bucketExists) {
      // Create bucket
      const { error: createError } = await supabase.storage.createBucket(DISEASE_IMAGES_BUCKET, {
        public: true,
        allowedMimeTypes: ['image/jpeg', 'image/png', 'image/webp'],
        fileSizeLimit: 10485760, // 10MB
      });

      if (createError) {
        console.error("Error creating bucket:", createError);
      } else {
        console.log(`Created storage bucket: ${DISEASE_IMAGES_BUCKET}`);
      }
    }
  } catch (error) {
    console.error("Error initializing storage bucket:", error);
  }
}

// Database connection test
export async function testSupabaseConnection(): Promise<boolean> {
  if (!supabaseUrl || !supabaseServiceKey) {
    console.warn("Supabase credentials not configured");
    return false;
  }

  try {
    // Test connection by listing tables
    const { data, error } = await supabase
      .from('users')
      .select('count(*)')
      .limit(1);

    if (error) {
      console.error("Supabase connection test failed:", error);
      return false;
    }

    console.log("Supabase connection successful");
    return true;
  } catch (error) {
    console.error("Supabase connection test failed:", error);
    return false;
  }
}

server/services/weather.ts.content:
import type { WeatherCache } from "@shared/schema";
import { storage } from "../storage";

interface WeatherResponse {
  location: string;
  temperature: number;
  condition: string;
  humidity: number;
  windSpeed: number;
  description: string;
  icon: string;
}

interface OpenWeatherAPIData {
  name: string;
  main: {
    temp: number;
    humidity: number;
    feels_like: number;
  };
  weather: Array<{
    main: string;
    description: string;
    icon: string;
  }>;
  wind: {
    speed: number;
  };
  sys: {
    country: string;
  };
}

export async function getCurrentWeather(location: string): Promise<WeatherResponse> {
  const apiKey = process.env.OPENWEATHER_API_KEY;

  if (!apiKey) {
    console.log("OpenWeather API key not configured, using mock data");
    return getMockWeatherData(location);
  }

  try {
    // Check cache first
    const cached = await getCachedWeather(location);
    if (cached && !isExpired(cached)) {
      return cached.weatherData as WeatherResponse;
    }

    // Fetch from OpenWeather API
    const response = await fetch(
      `https://api.openweathermap.org/data/2.5/weather?q=${encodeURIComponent(location)}&appid=${apiKey}&units=metric`
    );

    if (!response.ok) {
      throw new Error(`OpenWeather API error: ${response.status}`);
    }

    const data: OpenWeatherAPIData = await response.json();

    const weatherResponse: WeatherResponse = {
      location: `${data.name}, ${data.sys.country}`,
      temperature: Math.round(data.main.temp),
      condition: data.weather[0].main,
      humidity: data.main.humidity,
      windSpeed: Math.round(data.wind.speed * 3.6), // Convert m/s to km/h
      description: getFarmingAdvice(data.weather[0].main, data.main.temp, data.main.humidity),
      icon: data.weather[0].icon,
    };

    // Cache the result
    await cacheWeatherData(location, weatherResponse);

    return weatherResponse;
  } catch (error) {
    console.error("OpenWeather API error:", error);
    // Fallback to mock data on API failure
    return getMockWeatherData(location);
  }
}

function getMockWeatherData(location: string): WeatherResponse {
  // Generate realistic mock data based on location and current time
  const temperature = 25 + Math.floor(Math.random() * 10); // 25-35Â°C
  const conditions = ["Sunny", "Partly Cloudy", "Cloudy", "Light Rain"];
  const condition = conditions[Math.floor(Math.random() * conditions.length)];

  return {
    location: location || "Your Location",
    temperature,
    condition,
    humidity: 60 + Math.floor(Math.random() * 20), // 60-80%
    windSpeed: 8 + Math.floor(Math.random() * 8), // 8-16 km/h
    description: getFarmingAdvice(condition, temperature, 65),
    icon: getWeatherIcon(condition),
  };
}

function getFarmingAdvice(condition: string, temperature: number, humidity: number): string {
  const lowerCondition = condition.toLowerCase();

  if (lowerCondition.includes("rain")) {
    return "Good weather for transplanting. Ensure proper drainage to prevent waterlogging.";
  }

  if (lowerCondition.includes("sunny") && temperature > 30) {
    return "Hot weather - increase irrigation frequency and provide shade for sensitive crops.";
  }

  if (lowerCondition.includes("cloudy") && humidity > 80) {
    return "High humidity may promote fungal diseases. Ensure good air circulation and avoid overhead watering.";
  }

  if (temperature < 15) {
    return "Cool weather - protect sensitive crops from cold and reduce watering frequency.";
  }

  return "Good weather for most farming activities. Monitor soil moisture and adjust irrigation as needed.";
}

function getWeatherIcon(condition: string): string {
  const lowerCondition = condition.toLowerCase();

  if (lowerCondition.includes("sunny") || lowerCondition.includes("clear")) return "01d";
  if (lowerCondition.includes("partly cloudy")) return "02d";
  if (lowerCondition.includes("cloudy")) return "03d";
  if (lowerCondition.includes("rain")) return "10d";
  if (lowerCondition.includes("storm")) return "11d";
  if (lowerCondition.includes("snow")) return "13d";
  if (lowerCondition.includes("mist") || lowerCondition.includes("fog")) return "50d";

  return "01d"; // Default to sunny
}

function getWeatherIconFromCondition(condition: string): string {
  const lowerCondition = condition.toLowerCase();

  if (lowerCondition.includes("sunny") || lowerCondition.includes("clear")) return "01d";
  if (lowerCondition.includes("partly cloudy") || lowerCondition.includes("partly sunny")) return "02d";
  if (lowerCondition.includes("cloudy") || lowerCondition.includes("overcast")) return "03d";
  if (lowerCondition.includes("rain") || lowerCondition.includes("drizzle") || lowerCondition.includes("shower")) return "10d";
  if (lowerCondition.includes("thunder") || lowerCondition.includes("storm")) return "11d";
  if (lowerCondition.includes("snow") || lowerCondition.includes("blizzard")) return "13d";
  if (lowerCondition.includes("mist") || lowerCondition.includes("fog") || lowerCondition.includes("haze")) return "50d";

  return "01d"; // Default to sunny
}

// Cache management functions
async function getCachedWeather(location: string): Promise<WeatherCache | null> {
  // TODO: Implement with actual database
  // For now, return null to always fetch fresh data
  return null;
}

async function cacheWeatherData(location: string, data: WeatherResponse): Promise<void> {
  // TODO: Implement with actual database
  const cacheEntry = {
    location,
    weatherData: data,
    expiresAt: new Date(Date.now() + 30 * 60 * 1000), // 30 minutes
    createdAt: new Date(),
  };

  console.log("Weather data cached for:", location);
}

function isExpired(cached: WeatherCache): boolean {
  return new Date() > new Date(cached.expiresAt);
}

// Weather alerts and recommendations
export function getWeatherAlerts(weather: WeatherResponse): Array<{
  type: "info" | "warning" | "danger";
  message: string;
}> {
  const alerts = [];

  if (weather.temperature > 35) {
    alerts.push({
      type: "warning" as const,
      message: "High temperature alert! Ensure adequate irrigation and provide shade for crops.",
    });
  }

  if (weather.humidity > 85) {
    alerts.push({
      type: "warning" as const,
      message: "High humidity may increase disease risk. Monitor crops for fungal infections.",
    });
  }

  if (weather.windSpeed > 25) {
    alerts.push({
      type: "warning" as const,
      message: "Strong winds detected. Secure loose structures and protect tall crops.",
    });
  }

  if (weather.condition.toLowerCase().includes("rain")) {
    alerts.push({
      type: "info" as const,
      message: "Rain expected. Good for water-stressed crops but ensure proper drainage.",
    });
  }

  return alerts;
}

