const categories = ["All", "Financial Support", "Insurance", "Credit", "Price Support", "Equipment"];

export default function GovernmentSchemes() {
  const { t } = useTranslation();
  const [, setLocation] = useLocation();

  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("All");

  const filteredSchemes = schemes.filter(scheme => {
    const matchesSearch = scheme.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         scheme.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = selectedCategory === "All" || scheme.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  const getCategoryColor = (category: string) => {
    const colors = {
      "Financial Support": "bg-green-500/10 text-green-600 border-green-200",
      "Insurance": "bg-blue-500/10 text-blue-600 border-blue-200",
      "Credit": "bg-purple-500/10 text-purple-600 border-purple-200",
      "Price Support": "bg-orange-500/10 text-orange-600 border-orange-200",
      "Equipment": "bg-indigo-500/10 text-indigo-600 border-indigo-200"
    };
    return colors[category as keyof typeof colors] || "bg-gray-500/10 text-gray-600 border-gray-200";
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-emerald-50 to-teal-50 p-4 pb-20">
      {/* Header */}
      <div className="flex items-center justify-between mb-6 pt-4">
        <Button 
          variant="ghost" 
          size="icon"
          onClick={() => setLocation("/dashboard")}
          data-testid="button-back"
        >
          <ArrowLeft className="h-5 w-5" />
        </Button>
        <h1 className="text-2xl font-bold text-center flex-1" data-testid="schemes-title">
          Government Schemes
        </h1>
        <div className="w-10"></div>
      </div>

      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="max-w-4xl mx-auto space-y-6"
      >
        {/* Search and Filter */}
        <Card className="glass-enhanced shadow-lg border border-white/20" data-testid="search-filter-card">
          <CardContent className="p-4">
            <div className="flex flex-col sm:flex-row gap-4">
              <div className="relative flex-1">
                <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                <Input
                  placeholder="Search schemes..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                  data-testid="search-schemes"
                />
              </div>
              <div className="relative">
                <Filter className="absolute left-3 top-3 h-4 w-4 text-muted-foreground z-10" />
                <Select value={selectedCategory} onValueChange={setSelectedCategory}>
                  <SelectTrigger className="w-full sm:w-48 pl-10" data-testid="filter-category">
                    <SelectValue placeholder="Filter by category" />
                  </SelectTrigger>
                  <SelectContent>
                    {categories.map((category) => (
                      <SelectItem key={category} value={category}>
                        {category}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Schemes Grid */}
        <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
          {filteredSchemes.map((scheme, index) => (
            <motion.div
              key={scheme.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.4, delay: index * 0.1 }}
            >
              <Card 
                className="glass-enhanced shadow-lg border border-white/20 hover:shadow-xl transition-all duration-300 transform hover:-translate-y-2 h-full" 
                data-testid={`scheme-${scheme.id}`}
              >
                <CardHeader className="pb-3">
                  <div className="flex items-start gap-3">
                    <div className="w-12 h-12 bg-gradient-to-br from-emerald-500 to-teal-600 rounded-xl flex items-center justify-center">
                      <scheme.icon className="h-6 w-6 text-white" />
                    </div>
                    <div className="flex-1">
                      <CardTitle className="text-lg font-bold text-gray-900 mb-2" data-testid={`scheme-name-${scheme.id}`}>
                        {scheme.name}
                      </CardTitle>
                      <Badge 
                        variant="outline" 
                        className={getCategoryColor(scheme.category)}
                        data-testid={`scheme-category-${scheme.id}`}
                      >
                        {scheme.category}
                      </Badge>
                    </div>
                  </div>
                </CardHeader>

                <CardContent className="space-y-4">
                  <p className="text-sm text-gray-600" data-testid={`scheme-description-${scheme.id}`}>
                    {scheme.description}
                  </p>

                  <div className="space-y-2">
                    <div>
                      <p className="text-xs font-semibold text-gray-700 mb-1">Benefits:</p>
                      <p className="text-sm text-gray-600" data-testid={`scheme-benefits-${scheme.id}`}>
                        {scheme.benefits}
                      </p>
                    </div>

                    <div>
                      <p className="text-xs font-semibold text-gray-700 mb-1">Amount:</p>
                      <p className="text-sm font-bold text-emerald-600" data-testid={`scheme-amount-${scheme.id}`}>
                        {scheme.amount}
                      </p>
                    </div>

                    <div>
                      <p className="text-xs font-semibold text-gray-700 mb-1">Eligibility:</p>
                      <p className="text-xs text-gray-600" data-testid={`scheme-eligibility-${scheme.id}`}>
                        {scheme.eligibility}
                      </p>
                    </div>
                  </div>

                  <motion.div
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                  >
                    <Button 
                      className="w-full mt-4 bg-gradient-to-r from-emerald-500 to-teal-600 hover:from-emerald-600 hover:to-teal-700 text-white shadow-md hover:shadow-lg transition-all duration-300 font-semibold"
                      onClick={() => {
                        // Show a confirmation toast before redirect
                        window.open(scheme.applyLink, '_blank');
                      }}
                      data-testid={`button-apply-${scheme.id}`}
                    >
                      <ExternalLink className="mr-2 h-4 w-4" />
                      Apply on Official Website
                      <motion.div
                        className="ml-2"
                        animate={{ x: [0, 3, 0] }}
                        transition={{ duration: 1.5, repeat: Infinity }}
                      >
                        â†’
                      </motion.div>
                    </Button>
                  </motion.div>
                </CardContent>
              </Card>
            </motion.div>
          ))}
        </div>

        {filteredSchemes.length === 0 && (
          <Card className="glass-enhanced shadow-lg border border-white/20">
            <CardContent className="p-8 text-center">
              <Search className="mx-auto h-12 w-12 text-muted-foreground mb-4" />
              <h3 className="text-lg font-semibold text-gray-900 mb-2">No schemes found</h3>
              <p className="text-gray-600">Try adjusting your search or filter criteria.</p>
            </CardContent>
          </Card>
        )}

        {/* Disclaimer */}
        <Card className="glass-enhanced shadow-lg border border-white/20 bg-blue-50/50">
          <CardContent className="p-4">
            <div className="flex items-start gap-3">
              <Shield className="h-5 w-5 text-blue-600 mt-0.5" />
              <div>
                <p className="text-sm font-semibold text-blue-900 mb-1">Important Note</p>
                <p className="text-xs text-blue-800">
                  Please verify eligibility criteria and application procedures on official government websites. 
                  Scheme details may change periodically.
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </motion.div>
    </div>
  );
}

client/src/pages/profile.tsx.content:
import { useState } from "react";
import { motion } from "framer-motion";
import { useTranslation } from "react-i18next";
import { useLocation } from "wouter";
import { 
  ArrowLeft, 
  User, 
  Phone, 
  Globe, 
  MapPin, 
  Camera, 
  Edit, 
  Save,
  LogOut,
  Settings
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/lib/auth";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

export default function Profile() {
  const { t } = useTranslation();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const { user, updateUser, logout } = useAuth();

  const [isEditing, setIsEditing] = useState(false);
  const [editForm, setEditForm] = useState({
    name: user?.name || "",
    farmSize: user?.farmSize || "",
    location: user?.location || "",
    primaryCrops: user?.primaryCrops || [],
  });

  const updateProfileMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("PUT", "/api/user/profile", data);
      return response.json();
    },
    onSuccess: (data) => {
      updateUser(data.user);
      setIsEditing(false);
      toast({
        title: "Profile Updated",
        description: "Your profile has been updated successfully!",
      });
    },
    onError: (error) => {
      toast({
        title: "Update Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSaveProfile = () => {
    updateProfileMutation.mutate(editForm);
  };

  const handleLogout = () => {
    logout();
    setLocation("/");
  };

  if (!user) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <p className="text-lg text-muted-foreground">Loading profile...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-emerald-50 to-teal-50 p-4 pb-20">
      {/* Header */}
      <div className="flex items-center justify-between mb-6 pt-4">
        <Button 
          variant="ghost" 
          size="icon"
          onClick={() => setLocation("/dashboard")}
          data-testid="button-back"
        >
          <ArrowLeft className="h-5 w-5" />
        </Button>
        <h1 className="text-2xl font-bold text-center flex-1" data-testid="profile-title">
          {t("profile.title", "Profile")}
        </h1>
        <Button 
          variant="ghost" 
          size="icon"
          onClick={() => setIsEditing(!isEditing)}
          data-testid="button-edit"
        >
          {isEditing ? <Save className="h-5 w-5" /> : <Edit className="h-5 w-5" />}
        </Button>
      </div>

      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className="max-w-md mx-auto space-y-6"
      >
        {/* Profile Avatar & Basic Info */}
        <Card className="glass-enhanced shadow-lg border border-white/20" data-testid="profile-card">
          <CardContent className="pt-6">
            <div className="text-center mb-6">
              <div className="relative">
                <Avatar className="w-24 h-24 mx-auto mb-4" data-testid="profile-avatar">
                  <AvatarFallback className="bg-gradient-to-br from-emerald-500 to-teal-600 text-white text-2xl">
                    {user.name?.charAt(0).toUpperCase() || user.phone?.charAt(-2) || "F"}
                  </AvatarFallback>
                </Avatar>
                {isEditing && (
                  <Button
                    size="icon"
                    variant="secondary"
                    className="absolute bottom-0 right-1/2 transform translate-x-1/2 translate-y-1/2"
                    data-testid="button-change-avatar"
                  >
                    <Camera className="h-4 w-4" />
                  </Button>
                )}
              </div>

              {isEditing ? (
                <div className="space-y-3">
                  <div>
                    <Label htmlFor="name" className="text-sm font-medium">Name</Label>
                    <Input
                      id="name"
                      value={editForm.name}
                      onChange={(e) => setEditForm(prev => ({ ...prev, name: e.target.value }))}
                      placeholder="Enter your name"
                      data-testid="input-name"
                    />
                  </div>
                </div>
              ) : (
                <div>
                  <h2 className="text-2xl font-bold text-gray-900 mb-2" data-testid="profile-name">
                    {user.name || "Farmer"}
                  </h2>
                  <Badge variant="secondary" className="mb-2" data-testid="user-type">
                    <User className="w-3 h-3 mr-1" />
                    Farmer
                  </Badge>
                </div>
              )}
            </div>
          </CardContent>
        </Card>

        {/* Contact Information */}
        <Card className="glass-enhanced shadow-lg border border-white/20" data-testid="contact-card">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Phone className="h-5 w-5" />
              Contact Information
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex items-center gap-3">
              <Phone className="h-4 w-4 text-muted-foreground" />
              <span data-testid="profile-phone">{user.phone}</span>
            </div>
            <div className="flex items-center gap-3">
              <Globe className="h-4 w-4 text-muted-foreground" />
              <span data-testid="profile-language">
                {user.language === 'hi' ? 'à¤¹à¤¿à¤‚à¤¦à¥€' : 'English'}
              </span>
            </div>
          </CardContent>
        </Card>

        {/* Farm Information */}
        <Card className="glass-enhanced shadow-lg border border-white/20" data-testid="farm-card">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <MapPin className="h-5 w-5" />
              Farm Information
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            {isEditing ? (
              <div className="space-y-4">
                <div>
                  <Label htmlFor="location" className="text-sm font-medium">Farm Location</Label>
                  <Input
                    id="location"
                    value={editForm.location}
                    onChange={(e) => setEditForm(prev => ({ ...prev, location: e.target.value }))}
                    placeholder="Enter farm location"
                    data-testid="input-farm-location"
                  />
                </div>
                <div>
                  <Label htmlFor="farmSize" className="text-sm font-medium">Farm Size</Label>
                  <Input
                    id="farmSize"
                    value={editForm.farmSize}
                    onChange={(e) => setEditForm(prev => ({ ...prev, farmSize: e.target.value }))}
                    placeholder="e.g., 2 acres"
                    data-testid="input-farm-size"
                  />
                </div>
              </div>
            ) : (
              <div className="space-y-3">
                <div className="flex items-center gap-3">
                  <MapPin className="h-4 w-4 text-muted-foreground" />
                  <span data-testid="farm-location">
                    {user.location || "Not specified"}
                  </span>
                </div>
                <div className="flex items-center gap-3">
                  <Settings className="h-4 w-4 text-muted-foreground" />
                  <span data-testid="farm-size">
                    {user.farmSize || "Not specified"}
                  </span>
                </div>
                <div>
                  <p className="text-sm text-muted-foreground mb-2">Primary Crops:</p>
                  <div className="flex flex-wrap gap-2">
                    {user.primaryCrops && user.primaryCrops.length > 0 ? (
                      user.primaryCrops.map((crop, index) => (
                        <Badge key={index} variant="outline" data-testid={`crop-${index}`}>
                          {crop}
                        </Badge>
                      ))
                    ) : (
                      <span className="text-sm text-muted-foreground">Not specified</span>
                    )}
                  </div>
                </div>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Action Buttons */}
        <div className="space-y-3">
          {isEditing && (
            <Button 
              className="w-full font-bold text-lg py-6 bg-gradient-to-r from-emerald-500 to-teal-600 hover:from-emerald-600 hover:to-teal-700 shadow-lg hover:shadow-xl transform hover:-translate-y-1 transition-all duration-300"
              onClick={handleSaveProfile}
              disabled={updateProfileMutation.isPending}
              data-testid="button-save-profile"
            >
              {updateProfileMutation.isPending ? "Saving..." : "Save Changes"}
            </Button>
          )}

          <Separator />

          <Button 
            variant="outline" 
            className="w-full text-red-600 border-red-200 hover:bg-red-50"
            onClick={handleLogout}
            data-testid="button-logout"
          >
            <LogOut className="mr-2 h-4 w-4" />
            Logout
          </Button>
        </div>
      </motion.div>
    </div>
  );
}

client/src/pages/weather.tsx.content:
import { useState } from "react";
import { motion } from "framer-motion";
import { useTranslation } from "react-i18next";
import { useLocation } from "wouter";
import { ArrowLeft, CloudSun, Thermometer, Wind, Droplets, Eye, MapPin, Loader2, RefreshCw } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/lib/auth";
import { useToast } from "@/hooks/use-toast";

interface WeatherData {
  location: string;
  temperature: number;
  condition: string;
  humidity: number;
  windSpeed: number;
  description: string;
  icon: string;
}

interface WeatherAlert {
  type: "info" | "warning" | "danger";
  message: string;
}

export default function Weather() {
  const { t } = useTranslation();
  const [, setLocation] = useLocation();
  const { user } = useAuth();
  const { toast } = useToast();
  const [isGettingLocation, setIsGettingLocation] = useState(false);
  const [currentLocation, setCurrentLocation] = useState<string>(
    user?.location || "Mumbai, Maharashtra"
  );

  const { data: weather, isLoading, error, refetch } = useQuery<WeatherData>({
    queryKey: ["/api/weather/current", currentLocation],
    enabled: !!currentLocation,
    refetchInterval: 5 * 60 * 1000, // Refetch every 5 minutes
  });

  const getCurrentLocation = () => {
    if (!navigator.geolocation) {
      toast({
        title: "Location Error",
        description: "Geolocation is not supported by this browser.",
        variant: "destructive",
      });
      return;
    }

    setIsGettingLocation(true);
    navigator.geolocation.getCurrentPosition(
      async (position) => {
        try {
          const { latitude, longitude } = position.coords;
          const locationName = `${latitude},${longitude}`;
          setCurrentLocation(locationName);
          setIsGettingLocation(false);
          toast({
            title: "Location Updated",
            description: "Weather data updated for your current location.",
          });
        } catch (error) {
          setIsGettingLocation(false);
          toast({
            title: "Location Error",
            description: "Failed to get your location. Please try again.",
            variant: "destructive",
          });
        }
      },
      (error) => {
        setIsGettingLocation(false);
        let errorMessage = "Failed to get your location.";

        switch (error.code) {
          case error.PERMISSION_DENIED:
            errorMessage = "Location access denied. Please allow location access and try again.";
            break;
          case error.POSITION_UNAVAILABLE:
            errorMessage = "Location information is unavailable.";
            break;
          case error.TIMEOUT:
            errorMessage = "Location request timed out.";
            break;
        }

        toast({
          title: "Location Error",
          description: errorMessage,
          variant: "destructive",
        });
      },
      {
        enableHighAccuracy: true,
        timeout: 10000,
        maximumAge: 300000, // Cache for 5 minutes
      }
    );
  };

  const getTemperatureColor = (temp: number) => {
    if (temp <= 10) return "text-blue-600";
    if (temp <= 25) return "text-green-600";
    if (temp <= 35) return "text-yellow-600";
    return "text-red-600";
  };

  const getWeatherAlerts = (weather: WeatherData): WeatherAlert[] => {
    const alerts: WeatherAlert[] = [];

    if (weather.temperature > 35) {
      alerts.push({
        type: "warning",
        message: "High temperature warning. Ensure adequate water supply for crops and livestock.",
      });
    }

    if (weather.temperature < 10) {
      alerts.push({
        type: "warning",
        message: "Cold weather alert. Protect sensitive crops from frost damage.",
      });
    }

    if (weather.humidity > 80) {
      alerts.push({
        type: "info",
        message: "High humidity detected. Monitor crops for fungal diseases.",
      });
    }

    if (weather.windSpeed > 30) {
      alerts.push({
        type: "warning",
        message: "Strong winds expected. Secure loose structures and protect young plants.",
      });
    }

    if (weather.condition.toLowerCase().includes("rain")) {
      alerts.push({
        type: "info",
        message: "Rain expected. Good for water-stressed crops but ensure proper drainage.",
      });
    }

    return alerts;
  };

  if (error) {
    return (
      <div className="min-h-screen bg-background pb-20">
        <header className="bg-card border-b border-border p-6 flex items-center" data-testid="weather-header">
          <Button
            variant="ghost"
            size="icon"
            onClick={() => setLocation("/dashboard")}
            className="mr-4"
            data-testid="button-back"
          >
            <ArrowLeft className="h-5 w-5" />
          </Button>
          <h1 className="text-xl font-semibold" data-testid="page-title">
            {t("weather.title", "Weather")}
          </h1>
        </header>

        <div className="p-6 flex items-center justify-center min-h-[400px]">
          <Card className="text-center">
            <CardContent className="p-6">
              <CloudSun className="mx-auto h-16 w-16 text-muted-foreground mb-4" />
              <h3 className="text-lg font-semibold mb-2">Unable to load weather data</h3>
              <p className="text-muted-foreground mb-4">Please check your connection and try again.</p>
              <Button onClick={() => refetch()} data-testid="button-retry">
                <RefreshCw className="mr-2 h-4 w-4" />
                Try Again
              </Button>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background pb-20">
      {/* Header */}
      <header className="bg-card border-b border-border p-6 flex items-center" data-testid="weather-header">
        <Button
          variant="ghost"
          size="icon"
          onClick={() => setLocation("/dashboard")}
          className="mr-4"
          data-testid="button-back"
        >
          <ArrowLeft className="h-5 w-5" />
        </Button>
        <h1 className="text-xl font-semibold" data-testid="page-title">
          {t("weather.title", "Weather")}
        </h1>
        <div className="ml-auto">
          <Button
            variant="outline"
            size="sm"
            onClick={() => refetch()}
            disabled={isLoading}
            data-testid="button-refresh"
          >
            <RefreshCw className={`h-4 w-4 ${isLoading ? 'animate-spin' : ''}`} />
          </Button>
        </div>
      </header>

      <div className="p-6 space-y-6">
        {/* Location Controls */}
        <Card data-testid="location-card">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div className="flex items-center">
                <MapPin className="h-5 w-5 text-muted-foreground mr-2" />
                <div>
                  <p className="font-medium" data-testid="current-location">
                    {weather?.location || currentLocation}
                  </p>
                  <p className="text-sm text-muted-foreground">Current location</p>
                </div>
              </div>
              <Button
                variant="outline"
                onClick={getCurrentLocation}
                disabled={isGettingLocation}
                data-testid="button-get-location"
              >
                {isGettingLocation ? (
                  <Loader2 className="h-4 w-4 animate-spin" />
                ) : (
                  <MapPin className="h-4 w-4" />
                )}
                <span className="ml-2">
                  {isGettingLocation ? "Getting..." : "Use Current Location"}
                </span>
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Main Weather Card */}
        {isLoading ? (
          <Card data-testid="weather-loading">
            <CardContent className="p-6">
              <div className="animate-pulse">
                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    <div className="w-16 h-16 bg-muted rounded-full mr-6"></div>
                    <div>
                      <div className="h-8 bg-muted rounded w-32 mb-2"></div>
                      <div className="h-4 bg-muted rounded w-24"></div>
                    </div>
                  </div>
                  <div className="text-right">
                    <div className="h-12 bg-muted rounded w-20 mb-2"></div>
                    <div className="h-4 bg-muted rounded w-16"></div>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        ) : weather ? (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.4 }}
          >
            <Card data-testid="weather-card">
              <CardContent className="p-6">
                <div className="flex items-center justify-between mb-6">
                  <div className="flex items-center">
                    <div className="w-16 h-16 bg-primary/10 rounded-full flex items-center justify-center mr-6">
                      <CloudSun className="h-8 w-8 text-primary" />
                    </div>
                    <div>
                      <h2 className="text-2xl font-bold" data-testid="weather-condition">
                        {weather.condition}
                      </h2>
                      <p className="text-muted-foreground" data-testid="weather-description">
                        {weather.description}
                      </p>
                    </div>
                  </div>
                  <div className="text-right">
                    <p className={`text-4xl font-bold ${getTemperatureColor(weather.temperature)}`} data-testid="weather-temperature">
                      {weather.temperature}Â°C
                    </p>
                    <p className="text-muted-foreground text-sm">Temperature</p>
                  </div>
                </div>

                {/* Weather Details Grid */}
                <div className="grid grid-cols-3 gap-4">
                  <div className="text-center" data-testid="weather-humidity">
                    <Droplets className="h-6 w-6 text-blue-500 mx-auto mb-2" />
                    <p className="text-2xl font-semibold">{weather.humidity}%</p>
                    <p className="text-sm text-muted-foreground">Humidity</p>
                  </div>
                  <div className="text-center" data-testid="weather-wind">
                    <Wind className="h-6 w-6 text-green-500 mx-auto mb-2" />
                    <p className="text-2xl font-semibold">{weather.windSpeed} km/h</p>
                    <p className="text-sm text-muted-foreground">Wind Speed</p>
                  </div>
                  <div className="text-center" data-testid="weather-visibility">
                    <Eye className="h-6 w-6 text-purple-500 mx-auto mb-2" />
                    <p className="text-2xl font-semibold">Good</p>
                    <p className="text-sm text-muted-foreground">Visibility</p>
                  </div>
                </div>
              </CardContent>
            </Card>
          </motion.div>
        ) : null}

        {/* Weather Alerts */}
        {weather && (
          <div className="space-y-3">
            <h3 className="text-lg font-semibold" data-testid="alerts-title">
              Farming Alerts
            </h3>
            {getWeatherAlerts(weather).map((alert, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.3, delay: index * 0.1 }}
              >
                <Card data-testid={`alert-${index}`}>
                  <CardContent className="p-4">
                    <div className="flex items-start">
                      <Badge
                        variant={alert.type === "danger" ? "destructive" : alert.type === "warning" ? "default" : "secondary"}
                        className="mr-3 mt-0.5"
                      >
                        {alert.type}
                      </Badge>
                      <p className="text-sm text-foreground flex-1">{alert.message}</p>
                    </div>
                  </CardContent>
                </Card>
              </motion.div>
            ))}
            {getWeatherAlerts(weather).length === 0 && (
              <Card data-testid="no-alerts">
                <CardContent className="p-4 text-center">
                  <p className="text-muted-foreground">No weather alerts for your area.</p>
                </CardContent>
              </Card>
            )}
          </div>
        )}

        {/* Farming Recommendations */}
        {weather && (
          <Card data-testid="recommendations-card">
            <CardHeader>
              <CardTitle className="flex items-center">
                <Thermometer className="h-5 w-5 mr-2" />
                Today's Farming Recommendations
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                <div className="p-3 bg-muted/50 rounded-lg">
                  <h4 className="font-medium mb-1">Watering</h4>
                  <p className="text-sm text-muted-foreground">
                    {weather.humidity > 70 
                      ? "High humidity - reduce watering frequency"
                      : weather.temperature > 30
                      ? "Hot weather - increase watering, preferably early morning or evening"
                      : "Normal watering schedule recommended"}
                  </p>
                </div>
                <div className="p-3 bg-muted/50 rounded-lg">
                  <h4 className="font-medium mb-1">Field Work</h4>
                  <p className="text-sm text-muted-foreground">
                    {weather.condition.toLowerCase().includes("rain")
                      ? "Avoid field operations due to wet conditions"
                      : weather.windSpeed > 25
                      ? "Windy conditions - postpone spraying activities"
                      : "Good conditions for normal farm operations"}
                  </p>
                </div>
                <div className="p-3 bg-muted/50 rounded-lg">
                  <h4 className="font-medium mb-1">Pest Management</h4>
                  <p className="text-sm text-muted-foreground">
                    {weather.humidity > 80
                      ? "High humidity may increase fungal disease risk - monitor crops closely"
                      : weather.temperature > 35
                      ? "Hot weather may stress plants - watch for heat-related pest activity"
                      : "Normal pest monitoring recommended"}
                  </p>
                </div>
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

client/src/pages/ai-assistant.tsx.content:
import { useState, useRef, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useTranslation } from "react-i18next";
import { useLocation } from "wouter";
import { 
  ArrowLeft, 
  Send, 
  Mic, 
  MicOff, 
  Sparkles, 
  MessageCircle,
  Loader2,
  User,
  Bot,
  Leaf,
  Sun,
  TrendingUp
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent } from "@/components/ui/card";
import { ScrollArea } from "@/components/ui/scroll-area";
import { useToast } from "@/hooks/use-toast";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/lib/auth";

interface ChatMessage {
  id: string;
  text: string;
  isUser: boolean;
  timestamp: Date;
}

export default function AIAssistant() {
  const { t } = useTranslation();
  const [, setLocation] = useLocation();
  const { user } = useAuth();
  const { toast } = useToast();
  const scrollAreaRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  const [messages, setMessages] = useState<ChatMessage[]>([
    {
      id: "welcome",
      text: `Hello ${user?.name || "Farmer"}! ðŸ‘‹ I'm your AI farming assistant. I can help you with crop diseases, weather advice, farming techniques, and answer any agricultural questions you have. How can I assist you today?`,
      isUser: false,
      timestamp: new Date(),
    }
  ]);
  const [inputMessage, setInputMessage] = useState("");
  const [isListening, setIsListening] = useState(false);

  const sendMessageMutation = useMutation({
    mutationFn: async (message: string) => {
      const response = await apiRequest("POST", "/api/assistant/chat", { 
        message,
        context: {
          userLocation: user?.location,
          primaryCrops: user?.primaryCrops,
          farmSize: user?.farmSize
        }
      });
      return response.json();
    },
    onSuccess: (data, message) => {
      // Add user message
      const userMessage: ChatMessage = {
        id: Date.now().toString(),
        text: message,
        isUser: true,
        timestamp: new Date(),
      };

      // Add AI response
      const aiMessage: ChatMessage = {
        id: (Date.now() + 1).toString(),
        text: data.response,
        isUser: false,
        timestamp: new Date(),
      };

      setMessages(prev => [...prev, userMessage, aiMessage]);
      setInputMessage("");
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to send message. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSendMessage = () => {
    if (!inputMessage.trim() || sendMessageMutation.isPending) return;
    sendMessageMutation.mutate(inputMessage.trim());
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const quickQuestions = [
    {
      text: "What diseases affect my crops?",
      icon: Leaf,
      color: "bg-emerald-500/10 text-emerald-600",
    },
    {
      text: "Best planting time for my area?",
      icon: Sun,
      color: "bg-amber-500/10 text-amber-600",
    },
    {
      text: "How to improve crop yield?",
      icon: TrendingUp,
      color: "bg-blue-500/10 text-blue-600",
    },
  ];

  useEffect(() => {
    if (scrollAreaRef.current) {
      scrollAreaRef.current.scrollTop = scrollAreaRef.current.scrollHeight;
    }
  }, [messages]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-background via-background to-muted/30 pb-20">
      {/* Header */}
      <header className="sticky top-0 z-10 bg-gradient-to-r from-primary/5 via-primary/10 to-accent/5 border-b border-border/50 backdrop-blur-sm p-6">
        <div className="flex items-center justify-between">
          <Button
            variant="ghost"
            size="icon"
            onClick={() => setLocation("/dashboard")}
            className="mr-4"
            data-testid="button-back"
          >
            <ArrowLeft className="h-5 w-5" />
          </Button>

          <motion.div 
            className="flex items-center space-x-3 flex-1"
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
          >
            <motion.div
              className="w-10 h-10 bg-gradient-to-br from-emerald-500 to-teal-600 rounded-2xl flex items-center justify-center shadow-lg"
              animate={{ 
                scale: [1, 1.1, 1],
                rotate: [0, 5, -5, 0]
              }}
              transition={{ 
                duration: 4,
                repeat: Infinity,
                ease: "easeInOut"
              }}
            >
              <Bot className="text-white text-xl" />
            </motion.div>
            <div>
              <h1 className="text-xl font-bold bg-gradient-to-r from-foreground to-foreground/80 bg-clip-text text-transparent">
                AI Farm Assistant
              </h1>
              <p className="text-sm text-muted-foreground flex items-center">
                <motion.div
                  className="w-2 h-2 bg-green-500 rounded-full mr-2"
                  animate={{ scale: [1, 1.2, 1] }}
                  transition={{ duration: 2, repeat: Infinity }}
                />
                Online & Ready to Help
              </p>
            </div>
          </motion.div>

          <motion.div
            animate={{ rotate: [0, 360] }}
            transition={{ duration: 8, repeat: Infinity, ease: "linear" }}
          >
            <Sparkles className="h-6 w-6 text-primary" />
          </motion.div>
        </div>
      </header>

      {/* Chat Area */}
      <div className="flex flex-col h-[calc(100vh-140px)]">
        <ScrollArea className="flex-1 p-6" ref={scrollAreaRef}>
          <div className="max-w-3xl mx-auto space-y-4">
            <AnimatePresence>
              {messages.map((message, index) => (
                <motion.div
                  key={message.id}
                  initial={{ opacity: 0, y: 20, scale: 0.9 }}
                  animate={{ opacity: 1, y: 0, scale: 1 }}
                  exit={{ opacity: 0, y: -20, scale: 0.9 }}
                  transition={{ duration: 0.3, delay: index * 0.1 }}
                  className={`flex ${message.isUser ? "justify-end" : "justify-start"}`}
                >
                  <div className={`flex items-start space-x-3 max-w-[85%] ${message.isUser ? "flex-row-reverse space-x-reverse" : ""}`}>
                    {/* Avatar */}
                    <motion.div
                      className={`w-8 h-8 rounded-2xl flex items-center justify-center shadow-md ${
                        message.isUser 
                          ? "bg-gradient-to-br from-blue-500 to-blue-600" 
                          : "bg-gradient-to-br from-emerald-500 to-teal-600"
                      }`}
                      whileHover={{ scale: 1.1 }}
                    >
                      {message.isUser ? (
                        <User className="text-white text-sm" />
                      ) : (
                        <Bot className="text-white text-sm" />
                      )}
                    </motion.div>

                    {/* Message */}
                    <motion.div
                      className={`p-4 rounded-2xl shadow-sm ${
                        message.isUser
                          ? "bg-gradient-to-br from-blue-500 to-blue-600 text-white"
                          : "bg-card border border-border"
                      }`}
                      whileHover={{ scale: 1.02 }}
                    >
                      <p className="text-sm leading-relaxed whitespace-pre-wrap">
                        {message.text}
                      </p>
                      <p className={`text-xs mt-2 ${message.isUser ? "text-blue-100" : "text-muted-foreground"}`}>
                        {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                      </p>
                    </motion.div>
                  </div>
                </motion.div>
              ))}
            </AnimatePresence>

            {/* Typing indicator */}
            {sendMessageMutation.isPending && (
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="flex justify-start"
              >
                <div className="flex items-start space-x-3">
                  <div className="w-8 h-8 bg-gradient-to-br from-emerald-500 to-teal-600 rounded-2xl flex items-center justify-center">
                    <Bot className="text-white text-sm" />
                  </div>
                  <div className="bg-card border border-border p-4 rounded-2xl shadow-sm">
                    <div className="flex items-center space-x-2">
                      <motion.div
                        className="w-2 h-2 bg-primary rounded-full"
                        animate={{ scale: [1, 1.5, 1] }}
                        transition={{ duration: 0.8, repeat: Infinity, delay: 0 }}
                      />
                      <motion.div
                        className="w-2 h-2 bg-primary rounded-full"
                        animate={{ scale: [1, 1.5, 1] }}
                        transition={{ duration: 0.8, repeat: Infinity, delay: 0.2 }}
                      />
                      <motion.div
                        className="w-2 h-2 bg-primary rounded-full"
                        animate={{ scale: [1, 1.5, 1] }}
                        transition={{ duration: 0.8, repeat: Infinity, delay: 0.4 }}
                      />
                      <span className="text-sm text-muted-foreground ml-2">AI is thinking...</span>
                    </div>
                  </div>
                </div>
              </motion.div>
            )}

            {/* Quick Questions */}
            {messages.length === 1 && (
              <motion.div
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.5 }}
                className="pt-6"
              >
                <h3 className="text-lg font-semibold text-center mb-4 text-muted-foreground">
                  Quick Questions to Get Started
                </h3>
                <div className="grid gap-3">
                  {quickQuestions.map((question, index) => (
                    <motion.div
                      key={question.text}
                      initial={{ opacity: 0, x: -20 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: 0.7 + index * 0.1 }}
                    >
                      <Card 
                        className="cursor-pointer hover:shadow-lg transition-all duration-300 border-2 border-dashed border-muted-foreground/30 hover:border-primary/50"
                        onClick={() => {
                          setInputMessage(question.text);
                          inputRef.current?.focus();
                        }}
                      >
                        <CardContent className="p-4 flex items-center space-x-3">
                          <div className={`w-10 h-10 rounded-xl flex items-center justify-center ${question.color}`}>
                            <question.icon className="h-5 w-5" />
                          </div>
                          <p className="font-medium">{question.text}</p>
                        </CardContent>
                      </Card>
                    </motion.div>
                  ))}
                </div>
              </motion.div>
            )}
          </div>
        </ScrollArea>

        {/* Input Area */}
        <div className="border-t border-border p-4 bg-card/50 backdrop-blur-sm">
          <div className="max-w-3xl mx-auto">
            <div className="flex items-end space-x-3">
              <div className="flex-1 relative">
                <Input
                  ref={inputRef}
                  value={inputMessage}
                  onChange={(e) => setInputMessage(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="Ask me anything about farming..."
                  className="pr-12 py-3 text-base border-2 focus:border-primary/50 rounded-xl"
                  disabled={sendMessageMutation.isPending}
                  data-testid="input-message"
                />

                {/* Voice input button */}
                <Button
                  variant="ghost"
                  size="icon"
                  className="absolute right-2 top-1/2 transform -translate-y-1/2 hover:bg-primary/10"
                  onClick={() => setIsListening(!isListening)}
                  data-testid="button-voice"
                >
                  {isListening ? (
                    <MicOff className="h-4 w-4 text-red-500" />
                  ) : (
                    <Mic className="h-4 w-4" />
                  )}
                </Button>
              </div>

              <motion.div
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <Button
                  onClick={handleSendMessage}
                  disabled={!inputMessage.trim() || sendMessageMutation.isPending}
                  className="px-6 py-3 bg-gradient-to-r from-emerald-500 to-teal-600 hover:from-emerald-600 hover:to-teal-700 shadow-lg rounded-xl"
                  data-testid="button-send"
                >
                  {sendMessageMutation.isPending ? (
                    <Loader2 className="h-4 w-4 animate-spin" />
                  ) : (
                    <Send className="h-4 w-4" />
                  )}
                </Button>
              </motion.div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/price-alerts-dialo.content:
import { useState } from "react";
import { useTranslation } from "react-i18next";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Bell, Plus, Trash2, Edit, TrendingUp, TrendingDown } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

const createAlertSchema = z.object({
  commodity: z.string().min(1, "Commodity is required"),
  market: z.string().min(1, "Market is required"),
  targetPrice: z.number().positive("Target price must be positive"),
  priceUnit: z.string().optional(),
  alertType: z.enum(["above", "below"], { required_error: "Alert type is required" }),
});

type CreateAlertForm = z.infer<typeof createAlertSchema>;

interface PriceAlert {
  id: string;
  commodity: string;
  market: string;
  targetPrice: string;
  priceUnit: string;
  alertType: "above" | "below";
  isActive: boolean;
  createdAt: string;
}

const popularCrops = [
  "Rice", "Wheat", "Sugarcane", "Cotton", "Jowar", "Bajra", 
  "Maize", "Tur", "Gram", "Masoor", "Moong", "Urad",
  "Groundnut", "Sunflower", "Soybean", "Onion", "Potato", "Tomato"
];

const indianMarkets = [
  "Mumbai APMC", "Delhi Market", "Pune Market", "Nashik Market", 
  "Sangli Market", "Kolkata Market", "Chennai Market", "Bangalore Market", 
  "Hyderabad Market", "Ahmedabad Market"
];

interface PriceAlertsDialogProps {
  trigger?: React.ReactNode;
}

export default function PriceAlertsDialog({ trigger }: PriceAlertsDialogProps) {
  const { t } = useTranslation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isOpen, setIsOpen] = useState(false);
  const [isCreating, setIsCreating] = useState(false);

  const form = useForm<CreateAlertForm>({
    resolver: zodResolver(createAlertSchema),
    defaultValues: {
      commodity: "",
      market: "",
      targetPrice: 0,
      priceUnit: "per quintal",
      alertType: "above",
    },
  });

  // Fetch user's price alerts
  const { data: alerts = [], isLoading } = useQuery<PriceAlert[]>({
    queryKey: ["/api/price-alerts"],
    enabled: isOpen,
  });

  // Create price alert mutation
  const createAlertMutation = useMutation({
    mutationFn: (data: CreateAlertForm) => apiRequest("POST", "/api/price-alerts", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/price-alerts"] });
      form.reset();
      setIsCreating(false);
      toast({
        title: "Alert Created",
        description: "Your price alert has been set up successfully!",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create price alert",
        variant: "destructive",
      });
    },
  });

  // Delete price alert mutation
  const deleteAlertMutation = useMutation({
    mutationFn: (alertId: string) => apiRequest("DELETE", `/api/price-alerts/${alertId}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/price-alerts"] });
      toast({
        title: "Alert Deleted",
        description: "Price alert has been removed successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete price alert",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: CreateAlertForm) => {
    createAlertMutation.mutate(data);
  };

  const formatPrice = (price: string | number) => {
    const numPrice = typeof price === 'string' ? parseFloat(price) : price;
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0,
    }).format(numPrice);
  };

  const defaultTrigger = (
    <Button className="bg-accent text-accent-foreground hover:bg-accent/90" data-testid="button-setup-alerts">
      <Bell className="h-4 w-4 mr-2" />
      Setup Price Alerts
    </Button>
  );

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        {trigger || defaultTrigger}
      </DialogTrigger>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto" data-testid="price-alerts-dialog">
        <DialogHeader>
          <DialogTitle className="flex items-center">
            <Bell className="h-5 w-5 mr-2 text-accent" />
            Price Alerts Management
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Current Alerts */}
          <div>
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold">Your Active Alerts</h3>
              <Button
                onClick={() => setIsCreating(!isCreating)}
                size="sm"
                data-testid="button-create-alert"
              >
                <Plus className="h-4 w-4 mr-2" />
                New Alert
              </Button>
            </div>

            {isLoading ? (
              <div className="space-y-3">
                {Array.from({ length: 3 }).map((_, i) => (
                  <Card key={i}>
                    <CardContent className="p-4">
                      <div className="animate-pulse flex items-center justify-between">
                        <div className="flex items-center space-x-4">
                          <div className="w-10 h-10 bg-muted rounded-lg"></div>
                          <div>
                            <div className="h-4 bg-muted rounded w-32 mb-2"></div>
                            <div className="h-3 bg-muted rounded w-24"></div>
                          </div>
                        </div>
                        <div className="h-8 bg-muted rounded w-16"></div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            ) : alerts.length > 0 ? (
              <div className="space-y-3">
                {alerts.map((alert) => (
                  <Card key={alert.id} data-testid={`alert-card-${alert.id}`}>
                    <CardContent className="p-4">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center space-x-4">
                          <div className="w-10 h-10 bg-primary/10 rounded-lg flex items-center justify-center">
                            {alert.alertType === "above" ? (
